import React, { Component, useEffect } from 'react';

const ItemSingle = () => {
  const [item, setItem] = useState([]);

  useEffect(() => {

   fetch(`http://localhost:3020/api/packages/${this.props.id}`)
      .then(res => res.json())
      .then(item => {

        const itemData = item.map(items => (
          <>
          <div className="test">
            <h3>Packages: </h3>
            <p>{items.description}</p>
          </div>
          </>
        ));
        setItem(itemData);
      });
  }, [item]);
}

export default App
