import React, { Component } from 'react';

class App extends Component {
  constructor() {
      super()
      this.state = {
          loading: true,
          characters: []
      }
  }

componentDidMount() {
  fetch("localhost:3020")
  .then(response => response.json())
  .then(data => {
    console.log(data);
    this.setState({
      loading: false,
      characters: data.results
    });
  });
}
}

export default App
