import React, {Component} from 'react';

class Contact extends React.Component {

  constructor() {
    super();

    this.state = {
      name: '',
      comment:'',
      email:'',


      };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();


    const data = { name:this.state.name, comment:this.state.comment, email:this.state.email }

    fetch('http://localhost:3020/api/contacts', { method: 'POST', 

    body: JSON.stringify(data),

    headers:{ 'Content-Type': 'application/json' } })

    .then(res => res.json())

    .catch(error => console.error('Error:', error))

    .then(response => console.log('Success:', response));
   }

   handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="username">Enter name</label>
        <input id="name" name="name" type="text" onChange={this.handleChange} />

        <label htmlFor="email">Enter comment</label>
        <input id="comment" name="comment" type="text" onChange={this.handleChange} />

        <label htmlFor="password">Enter email</label>
        <input id="email" name="email" type="email" onChange={this.handleChange} />

        <button>Send</button>
      </form>
    );
  }
}

export default Contact;
