import React, { Component } from 'react';

const CharRow = (props) => {
  const id = props.id + 1;
  const {name, birth_year} = props.char;
  return (
    <tr>
      <td>{id}</td>
      <td>{name}</td>
      <td>{birth_year}</td>
    </tr>
  )
}

const Loading = () => {
  return (
    <tr>
      <td></td>
      <td>Loading&hellip;</td>
      <td></td>
      <td></td>
    </tr>
  )
}

class App extends Component {
  constructor() {
      super()
      this.state = {
          loading: true,
          characters: []
      }
  }

  componentDidMount() {
    fetch("https://swapi.co/api/people/")
    .then(response => response.json())
    .then(data => {
      console.log(data);
      this.setState({
        loading: false,
        characters: data.results
      });
    });
  }

  render() {
    const charRows = this.state.loading ? 
      <Loading /> : 
      this.state.characters.map((charObj, idx) => <CharRow key={idx} id={idx} char={charObj} />);

      return (
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Name</th>
                <th>Birth Year</th>
              </tr>
            </thead>
            <tbody>
              {charRows}
            </tbody>
          </table>
      )
  }
}









export default App
