let comment= document.getElementById('')
  let email= document.getElementById('')
  let name= document.getElementById('')
  const addComment = event => {
    console.log(comment.value);

    fetch(`http://localhost:3000/api/contacts/create/${comment.value}/${email.value}/${name.value}`, {method: 'POST'})
    .then(respose => respose.json())
    .then(data => {
      console.log(data);
    });
    window.location.reload();
  }

const Contact = () => {
  const [contact, setContact] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3020/api/contacts`)
    .then(respose => respose.json())
    .then(data => {
      setContact(data.map(sub => (
        <>
        <label htmlFor="name">Enter name</label>
        <input id="name" name="name" type="text" onChange={this.handleChange} />

        <label htmlFor="comment">Enter comment</label>
        <input id="comment" name="comment" type="text" onChange={this.handleChange} />

        <label htmlFor="email">Enter email</label>
        <input id="email" name="email" type="email" onChange={this.handleChange} />

        <button>Send</button>
      </>
      )));
    });
  },[])


  
  const handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  } 


  return (
    <form action="">
        {contacts}
    </form>
  );
  }

export default Contact;
