import React, {Component} from 'react';

class Contact extends React.Component {

  constructor() {
    super();

    this.state = {
      username: '',
      email:'',
      password:'',


      };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();


    const data = { username:this.state.username, email:this.state.email , password:this.state.password }

    fetch('/api/createAccount', { method: 'POST', 

    body: JSON.stringify(data), // data can be `string` or {object}!

    headers:{ 'Content-Type': 'application/json' } })

    .then(res => res.json())

    .catch(error => console.error('Error:', error))

    .then(response => console.log('Success:', response));
   }



  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="username">Enter name</label>
        <input id="name" name="name" type="text" />

        <label htmlFor="email">Enter  comment</label>
        <input id="comment" name="comment" type="text" />

        <label htmlFor="password">Enter email</label>
        <input id="email" name="email" type="email" />

        <button>Send data!</button>
      </form>
    );
  }
}

export default Contact;
